import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import { Button } from "@material-ui/core";
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from '@material-ui/core/Grid';

// firebase
// import { auth } from "../../config/firebase";

class Login extends React.Component {

    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        // initial states
        this.state = {
            loading: false,
            email: "",
            password: "",
        }

        this.on_submit = this.on_submit.bind(this);

    }

    componentDidMount() {

        // this.setState({
        //     loading: true,
        // });

        // // check if user is logged
        // auth.onAuthStateChanged((user) => {

        //     if (user) {

        //         this.props.history.push('/');

        //     }

        //     else {

        //         // console.log("user no logged");

        //         // this.props.history.push('/login/');
        //     }

        //     this.setState({
        //         loading: false,
        //     });

        // });

    }

    // onsubmit form
    on_submit() {

        this.setState({
            loading: true,
        });

        const email = this.state.email.trim();
        const password = this.state.password;

        // console.log(this);
        // auth.signInWithEmailAndPassword(email, password)

        //     .then(res => {

        //         console.log("user logged!");

        //         this.setState({
        //             loading: false,
        //         });

        //         this.props.history.push('/admin');

        //     })

        //     .catch((error) => {

        //         this.setState({
        //             loading: false,
        //         });

        //         console.log(error.code);

        //         alert(error.message);

            // });
    }

    render() {

        return (

            <Grid container spacing={3}>

                {/* information
                <Grid item xs={12} sm={7}
                // style = {{backgroundColor: "red"}}
                >

                    <Paper
                        elevation={3}
                        style={{
                            padding: 20,
                            margin: 10,
                            alignContent: "center",
                            justifyContent: "center",
                            display: "flex",
                            flexDirection: "column",
                        }}
                    >

                        <Paper style={{ padding: 10, margin: 5 }} elevation={3}>
                            <Typography align="center" variant="h4" component="h4" gutterBottom>
                                Welcome to the course maker of Lanlearner!
                            </Typography>

                            <Typography align="center" variant="body2" component="p" gutterBottom>
                                This is a platform to build an English course and then publish it in the Lanlearner app
                            </Typography>
                        </Paper>

                    </Paper>
                </Grid> */}


                {
                    !this.state.loading

                        ?

                        <Grid item xs={12} sm={5}
                        // style={{ backgroundColor: "yellow" }}
                        >
                            <Paper
                                style={{
                                    padding: 20,
                                    margin: 10,
                                    alignContent: "center",
                                    justifyContent: "center",
                                    display: "flex",
                                    flexDirection: "column",
                                }}

                                elevation={3}
                            >

                                <Typography align="center" variant="h4" component="h4" gutterBottom>
                                    Enter to the platform
                                </Typography>

                                <Typography align="center" variant="body2" component="p" gutterBottom>
                                    Enter your email and password
                                </Typography>


                                <FormControl
                                    style={{
                                        width: "50%",
                                        alignSelf: "center",
                                    }}
                                >

                                    <TextField
                                        id="standard-uncontrolled"
                                        label="Email"
                                        // defaultValue="Correo electr贸nico"
                                        margin="normal"
                                        onChange={(e) => this.setState({ email: e.target.value })}
                                        value={this.state.email}
                                    />

                                    <TextField
                                        id="standard-uncontrolled"
                                        label="Password"
                                        type="password"
                                        // defaultValue="Correo electr贸nico"
                                        margin="normal"
                                        onChange={(e) => this.setState({ password: e.target.value })}
                                        value={this.state.password}
                                    />

                                    <Button align="center" variant="contained" color="primary" onClick={this.on_submit}>
                                        Enter
                                    </Button>

                                </FormControl>

                                <Typography align="center" variant="body2" component="p" gutterBottom>
                                    If you don't remember your password, please contact the platform admin
                                </Typography>

                                {/* register area */}
                                <Container
                                    style={{
                                        display: "flex",
                                        flexDirection: "column",
                                        justifyContent: "center",
                                        alignItems: "center",
                                    }}
                                >

                                    {/* information */}
                                    <Typography align="center" variant="body2" component="p" gutterBottom>
                                        If you do not have account yet, please register here
                                    </Typography>

                                    {/* register button */}
                                    <Button align="center" variant="contained" color="primary"
                                        onClick={() => {
                                            this.props.history.push('/register');
                                        }}
                                    >
                                        Sign-up
                                    </Button>
                                </Container>

                            </Paper>

                        </Grid>

                        :

                        <CircularProgress />

                }
                {/* </Paper> */}
            </Grid>
        );

    }

}

export default Login;
