import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
// import TextField from '@material-ui/core/TextField';
import { Button } from "@material-ui/core";
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from '@material-ui/core/Grid';

// import Card from '@material-ui/core/Card';
// import CardActionArea from '@material-ui/core/CardActionArea';
// import CardActions from '@material-ui/core/CardActions';
// import CardContent from '@material-ui/core/CardContent';
// import CardMedia from '@material-ui/core/CardMedia';
// // import Modal from '@material-ui/core/Modal';
// import PlayArrow from '@material-ui/icons/PlayArrow';
// // import Schedule from '@material-ui/icons/Schedule';

// import Select from '@material-ui/core/Select';

// import Dialog from '@material-ui/core/Dialog';
// import DialogActions from '@material-ui/core/DialogActions';
// import DialogContent from '@material-ui/core/DialogContent';
// import DialogTitle from '@material-ui/core/DialogTitle';
// import Chip from '@material-ui/core/Chip';

// firebase
// import { auth } from "../../config/firebase";


// prototype post
const posts = [

    {
        "text": "hello this is my first IG post",
        "image": "https://www.biggerbolderbaking.com/wp-content/uploads/2017/08/1C5A0056.jpg",
    },

    {
        "text": "This is another IG post",
        "image": "https://www.biggerbolderbaking.com/wp-content/uploads/2017/08/1C5A0056.jpg",
    },
];


class EditSNPostToProduct extends React.Component {

    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        // initial states
        this.state = {
            loading: false,
            post: posts[0],
            productName: null,
            productDescription: null,
            // this can be for example "size" or "units"
            productVar1: null,
            productPrice: null,
            productImage: null,
        }

    }

    componentDidMount() {

        // console.log(this.state.posts);

        // this.setState({
        //     loading: true,
        // });

        // // check if user is logged
        // auth.onAuthStateChanged((user) => {

        //     if (user) {

        //         this.props.history.push('/');

        //     }

        //     else {

        //         // console.log("user no logged");

        //         // this.props.history.push('/EditSNPostToProduct/');
        //     }

        //     this.setState({
        //         loading: false,
        //     });

        // });

    }

    render() {

        return (

            <Grid
                container
                spacing={3}
            >


                {
                    !this.state.loading

                        ?

                        <Grid item xs={12} sm={12}
                        // style={{ backgroundColor: "yellow" }}
                        >

                            {/* list of posts */}
                            <Paper
                                style={{
                                    padding: 20,
                                    margin: 10,
                                    alignContent: "center",
                                    justifyContent: "center",
                                    display: "flex",
                                    flexDirection: "column",
                                }}

                                elevation={3}
                            >

                                {/* section title */}
                                <Typography align="center" variant="h4" component="h4" gutterBottom>
                                    Editar post para convertir en producto
                                </Typography>

                                {/* information */}

                                {/* original information */}
                                <Container>

                                    {/* original image */}
                                    <img 
                                        src={this.state.post.image} 
                                        alt="image" 
                                        width = "300px"
                                        heigh = "300px"
                                    />

                                    {/* originla text */}
                                    <Typography align="center" variant="body2" component="p" gutterBottom>
                                        {this.state.post.text}
                                    </Typography>

                                </Container>

                                {/* informatino to fill */}
                                <Container>

                                    {/* product name */}
                                    <TextField
                                        // id="standard-uncontrolled"
                                        label="Nombre producto"
                                        type="Nombre producto"
                                        // defaultValue="Correo electrónico"
                                        margin="normal"
                                        onChange={(e) => this.setState({ password: e.target.value })}
                                        value={this.state.password}
                                    />



                                </Container>

                            </Paper>

                        </Grid>

                        :

                        <CircularProgress />

                }
                {/* </Paper> */}
            </Grid>
        );

    }

}

export default EditSNPostToProduct;
